from django.db.models.signals import post_savefrom django.dispatch import receiverfrom allauth.socialaccount.models import SocialAccountfrom .models import ProfileModel@receiver(post_save, sender=SocialAccount)def ProfileSaveSignals(sender, instance, created, **kwargs):    if created:        ProfileModel.objects.create(            social_account=instance,            email=instance.user.email,            name=instance.extra_data.get('name', ''),            picture=instance.extra_data.get('picture', '')        )